import React, { useState, useEffect, useRef } from "react";
import { StyleSheet, View, Dimensions, ActivityIndicator } from "react-native";
import { Text, Button, Card, Overlay } from "react-native-elements";
import { Camera } from "expo-camera";
import * as ImageManipulator from "expo-image-manipulator";
import * as tf from "@tensorflow/tfjs";
import "@tensorflow/tfjs-react-native";
import * as cocoSsd from "@tensorflow-models/coco-ssd";
import * as jpeg from "jpeg-js";
export default function App() {
  const statusList = [
    "Loading Model...",
    "Classifying Image...",
    "Predicting Image...",
  ];
  const [hasPermission, setHasPermission] = useState(null);
  const [type, setType] = useState(Camera.Constants.Type.back);
  const [isTfReady, setIsTfReady] = useState(false);
  const [cocoSsdModel, setCocoSsdModel] = useState(null);
  const cameraRef = useRef();
  const [status, setStatus] = useState(statusList[0]);
  const [isLoading, setIsLoading] = useState(true);
  useEffect(() => {
    (async () => {
      if (!isTfReady) {
        console.log("[+] Loading TF Model");
        setStatus(statusList[0]);
        setIsLoading(true);
        let { status } = await Camera.requestPermissionsAsync();
        setHasPermission(status === "granted");
        await tf.ready();
        setIsTfReady(true);
        setCocoSsdModel(await cocoSsd.load());
        setIsLoading(false);
        console.log("[+] TF Model Loaded");
      }
    })();
  }, [isTfReady]);

  const getPredictions = async () => {
    if (cameraRef.current) {
      console.log("[+] Analysing Photo");
      setStatus(statusList[2]);
      setIsLoading(true);
      const photo = await cameraRef.current.takePictureAsync({
        skipProcessing: true,
      });
      const image = await resizeImage(photo.uri, 224, 224);
      const imageTensor = base64ImageToTensor(image.base64);
      const model = await cocoSsd.load();
      const detections = await model.detect(imageTensor);
      console.log("detections", detections);
      setIsLoading(false);
      console.log("[+] Photo Analysed");
    }
  };

  function base64ImageToTensor(base64) {
    //Function to convert jpeg image to tensors
    const rawImageData = tf.util.encodeString(base64, "base64");
    const TO_UINT8ARRAY = true;
    const { width, height, data } = jpeg.decode(rawImageData, TO_UINT8ARRAY);
    // Drop the alpha channel info for mobilenet
    const buffer = new Uint8Array(width * height * 3);
    let offset = 0; // offset into original data
    for (let i = 0; i < buffer.length; i += 3) {
      buffer[i] = data[offset];
      buffer[i + 1] = data[offset + 1];
      buffer[i + 2] = data[offset + 2];
      offset += 4;
    }
    return tf.tensor3d(buffer, [height, width, 3]);
  }

  async function resizeImage(imageUrl, width, height) {
    const actions = [{ resize: { width, height } }];
    const saveOptions = {
      compress: 0.75,
      format: ImageManipulator.SaveFormat.JPEG,
      base64: true,
    };
    const res = await ImageManipulator.manipulateAsync(
      imageUrl,
      actions,
      saveOptions
    );
    return res;
  }

  return (
    <View style={styles.container}>
      <Overlay
        isVisible={isLoading}
        fullScreen={true}
        overlayStyle={{ alignItems: "center", justifyContent: "center" }}
      >
        <View>
          <Text style={{ marginBottom: 10 }}>{status}</Text>
          <ActivityIndicator size="large" color="lightblue" />
        </View>
      </Overlay>
      <View style={{ width: 224, height: 224 }}>
        <Camera style={{ flex: 1 }} type={type} ref={cameraRef}></Camera>
      </View>

      <View style={{ flexDirection: "row", padding: 5 }}>
        <View style={{ flex: 1, padding: 5 }}>
          <Button
            title="Predict"
            onPress={() => {
              getPredictions();
            }}
          />
        </View>
        <View style={{ flex: 1, padding: 5 }}>
          <Button
            title="flip"
            onPress={() => {
              getPredictions();
            }}
          />
        </View>
      </View>
    </View>
  );
}
const styles = StyleSheet.create({
  container: {
    flexDirection: "column",
    flex: 1,
    alignItems: "center",
    justifyContent: "center",
    padding: 10,
  },
});
